    private void comboBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_comboBox1ActionPerformed
        // TODO add your handling code here:
        if(comboBox1.getSelectedIndex() == 1){
            new Kambo().setVisible(true);
        }else if(comboBox1.getSelectedIndex() == 2){
            new PantaiLabombo().setVisible(true);
        }else if(comboBox1.getSelectedIndex() == 3){
            new ArterLatuppa().setVisible(true);
        }else if(comboBox1.getSelectedIndex() == 4){
            new SungaiJodoh().setVisible(true);
        }else if(comboBox1.getSelectedIndex() == 5){
            new Bora().setVisible(true);
        }else if(comboBox1.getSelectedIndex() == 6){
            new Agro().setVisible(true);
        }else if(comboBox1.getSelectedIndex() == 7){
            new LapPancasila().setVisible(true);
        }else if(comboBox1.getSelectedIndex() == 8){
            new PelTanjRinggit().setVisible(true);
        }else if(comboBox1.getSelectedIndex() == 9){
            new IstanaKedLuwu().setVisible(true);
        }
       
      

    }//GEN-LAST:event_comboBox1ActionPerformed

